import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  template: `
    <div class="center">
      <h1>PHOENIX CINEMA</h1>
    </div>
  `,
  styles: [
    `
    
    .center {
      line-height: calc( 100vh - 300px); 
      height: calc( 100vh - 80px);;
      text-align: center;
    }
    
    .center h1 {
      line-height: 1;
      display: inline-block;
      vertical-align: middle;
      color:white;
    }
  `,
  ]
})
export class HomeComponent {

}
