import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  template: `
    <div class="center">
      <h1>PHOENIX CINEMA</h1>
      <button>In programmazione</button>
    </div>
  `,
  styles: [
    `
    @font-face {
      font-family: argentum-semibolditalic;
      src: url("/assets/fonts/ArgentumNovus-SemiBoldItalic.ttf");
    }

    .center {
      /*background: rgb(255,38,0);
      background: linear-gradient(142deg, rgba(255,38,0,1) 0%, rgba(255,90,0,1) 64%, rgba(255,110,0,1) 86%, rgba(255,149,0,1) 100%);
      */
      height: calc( 100vh - 80px);
      background-position: center;
      background-repeat: no-repeat;
      background-size: cover;
      /* text-align: center; */
      display: flex;
      flex-direction: column;
      align-items: center;
      justify-content: center;
      text-align: center;
    }
    
    h1 {
      color:white;
      font-family:argentum-semibolditalic;
      padding: 5px;
      margin-bottom: 20px;
    }
    
    button {
      padding: 10px;
      border: 10px;
      border: 1px solid black;
      border-color: white;
      border-radius: 5px;
      color:white;
      background: rgba(250,108,20,0.2);
      font-family: argentum-regular;
    }
    button:hover{
      border-color: #fa6c14;
      background: #fa6c14;

    }
  `,
  ]
})
export class HomeComponent {

}
