import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  template: `
    <div class="center">
      <h1>PHOENIX CINEMA</h1>
      <br>
      <button>ciao</button>
    </div>
  `,
  styles: [
    `
    @font-face {
      font-family: argentum;
      src: url("/assets/fonts/ArgentumNovus-SemiBoldItalic.ttf");
    }

    .center {
      background-image: url("/assets/image/background.jpg");
      /*background: rgb(255,38,0);
      background: linear-gradient(142deg, rgba(255,38,0,1) 0%, rgba(255,90,0,1) 64%, rgba(255,110,0,1) 86%, rgba(255,149,0,1) 100%);
      */
      height: calc( 100vh - 80px);

      background-position: center;
      background-repeat: no-repeat;
      background-size: cover;
      text-align: center;
        display: flex;
        align-items: center; 
    }
    
    .center h1 {
      line-height: 1;
      display: inline-block;
      vertical-align: middle;
      color:white;
      background:red;
      font-family:argentum;
    }
    
    .center button {
      line-height: 1;
      display: inline-block;
      vertical-align: middle;
      color:white;
      background:red;
      font-family:argentum;
    }
  `,
  ]
})
export class HomeComponent {

}
