import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { Util } from '../services/util';

@Component({
  selector: 'app-login',
  template: `
    <div id="userDiv"> 
      <br><br><br>LOGIN
      <form #loginForm="ngForm" (ngSubmit)="login(loginForm.value)">
        <input type="text" name="email" ngModel placeholder="email"><br><br>
        <input type="password" name="password" ngModel placeholder="password"><br><br>
        <button type="submit">login</button>

      </form>


    </div>
  `,
  styles: [
  ]
})
export class LoginComponent {
  constructor(private http: HttpClient){ }

  login(data: any){
    // console.log(data)
    this.http.post(Util.authServerUrl+"login",data).subscribe(
      success => {
        console.log(success.valueOf())
        
        console.log(JSON.stringify(success))
       
        console.log(JSON.stringify(success)) //bruno@bruno.it
      },
      error => console.log(error.error)
    )
  }

}
