import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  template: `
    <app-header></app-header>

    <router-outlet></router-outlet>

    <app-footer></app-footer> 
  `,
  styles: [`
  /* UTILITIES */
 
  footer{
    position: fixed;
    left: 0;
    bottom: 0;
    width: 100%;
    color: black;
    text-align: center;
  }
  `,]
})
export class AppComponent {
  title = 'frontend';
}
