import { Component } from '@angular/core';

@Component({
  selector: 'app-footer',
  template: `
    <footer class="navbar-brand">
      <div class="container content has-text-centered">
        <p>Made by Andrea, Davide, Domenico, Bruno</p>
      </div>
  </footer>
  `,
  styles: [
    `
    <navbar-brand{
      
      position: fixed;
      left: 0;
      bottom: 0;
      width: 100%;
      background-color: red;
      color: white;
      text-align: center;
    }
    `,
  ]
})
export class FooterComponent {

}
