import { Component } from '@angular/core';

@Component({
  selector: 'app-header',
  template: `
    <div class="navbar">

     <div class="navbar-brand">

       <a class="navbar-item"routerLink="/">
         <img src="assets/image/logo-phoenix.png" >
       </a>

     </div>
     <div class="navbar-menu">

       <a class="navbar-item" routerLink="/user">user</a>
     </div>
   </div>
  `,
  styles: [
    `
    a {
      padding-right : 5px;
    }
    .navbar {
      display: flex;
      align-items: center;
      background-color: #c55a11;

  overflow: hidden;
    }
    .navbar img{

      height :20x;
    }
    /*nside the navigation bar */
    .navbar a {
      float: left;
      display: block;
      color: #f2f2f2;
      text-align: center;
      padding: 14px 16px;
      text-decoration: none;
      font-size: 17px;
    }

/* Change the color of links on hover */
.navbar a:hover {
  background-color: #ddd;
  color: black;
}

/* Add an active class to highlight the current page */
.navbar a.active {
  background-color: #04AA6D;
  color: white;
}

    `,
  ]
})
export class HeaderComponent {

}
