import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  template: `
    <div class="center">
      <p>PHOENIX CINEMA HOME</h1>
    </div>
  `,
  styles: [
    `
    .center {
      line-height: 200px;
      height: 100vh;
      border: 3px solid green;
      text-align: center;
    }
    
    .center p {
      line-height: 1.5;
      display: inline-block;
      vertical-align: middle;
    }
  `,
  ]
})
export class HomeComponent {

}
