import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  template: `
    <app-header></app-header>
    <div class="body">
      <div class="inner-body">
        <router-outlet></router-outlet>
      </div>
    <app-footer></app-footer>
    </div>
  `,
  styles: [`
  /* UTILITIES */


  footer {
}

main {
}
  .body{
  height: calc(100% - 120px);
  /* height of nav and footer is excluded from 100% */
  overflow: auto;
  margin: 65px 0 60px 0;
  }
  .inner-body{
    flex: 1;
  }
 
  app-footer{
  position: fixed;
  bottom: 0;
  left: 0;
  height: 60px;
  background: #000;
  width: 100%;
    /*
    width: 100%;
    color: black;
    text-align: center;
    /*
    position:fixed;
    bottom:0;*/
  }
  `,]
})
export class AppComponent {
  title = 'frontend';
}
