import { Component } from '@angular/core';

@Component({
  selector: 'app-header',
  template: `
    <div class="navbar">
     
     <div class="navbar-brand">
       
       <a class="navbar-item">
         <img src="assets/image/logo-phoenix.png">
       </a>
       
     </div>
     <div class="navbar-menu">
       
       <a class="navbar-item" routerLink="/">Home</a>
       <a class="navbar-item" routerLink="/user">user</a>
     </div>
   </div>
  `,
  styles: [
    `
    a {
      padding-right : 5px;
    }
    .navbar {
      display: flex;
      align-items: center;
      justify-content: space-between;
      padding: 20px;
      background-color: #c55a11;
      color: #000;
      
    }
    .navbar-item{
      height:10px
    }
    .header {
        width: 1210px;
        height: 192px;
        margin: 0 auto;
    }
    .header img {
        position: absolute;
        max-width: 100%;
        max-height: 100%;
        margin: 0 auto;
    }
    `,
  ]
})
export class HeaderComponent {

}
